import React from 'react';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';

function WeeklyPdfExport() {
  const stored = JSON.parse(localStorage.getItem('gutTrackerEntries')) || {};

  const getWeekNumber = (dateStr) => {
    const start = new Date("2025-04-02");
    const current = new Date(dateStr);
    const diff = Math.floor((current - start) / (1000 * 60 * 60 * 24));
    return Math.floor(diff / 7) + 1;
  };

  const groupEntriesByWeek = () => {
    const weeks = {};
    Object.keys(stored).forEach((date) => {
      const weekNum = getWeekNumber(date);
      if (!weeks[weekNum]) weeks[weekNum] = [];
      weeks[weekNum].push({ date, ...stored[date] });
    });
    return weeks;
  };

  const formatValue = (val) => {
    if (typeof val === 'boolean') return val ? 'Yes' : 'No';
    if (val === null || val === undefined || val === '') return 'N/A';
    return String(val);
  };

  const capitalize = (str) =>
    str.replace(/([A-Z])/g, ' $1').replace(/^./, (s) => s.toUpperCase());

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(13);

    // Page 1: Title & Stool Chart
    doc.text("Gut Health Tracker – Praneeth Kumar", 10, 10);
    doc.setFontSize(11);
    doc.text("Bristol Stool Chart", 10, 30);

    // ⛔ Replace with actual Base64 image string of your stool chart
    const stoolChartImage = 'data:image/png;base64,'; // <-- paste your base64 image here

    try {
      doc.addImage(stoolChartImage, 'PNG', 10, 35, 180, 100);
    } catch (err) {
      doc.setFontSize(10);
      doc.text('Unable to load Bristol Stool Chart image.', 10, 140);
    }

    doc.addPage();

    // Weekly Entries Pages
    const weekGroups = groupEntriesByWeek();
    const weekNums = Object.keys(weekGroups).sort((a, b) => a - b);
    let isFirstEntryPage = true;

    weekNums.forEach((weekNum) => {
      const entries = weekGroups[weekNum];
      if (!entries) return;

      entries.forEach((entry) => {
        if (!isFirstEntryPage) doc.addPage();
        isFirstEntryPage = false;

        let y = 20;

        doc.setFontSize(11);
        doc.text(`Entry Date: ${entry.date}`, 10, y);
        y += 6;

        const routineData = [];
        const extraDetails = [];
        let yogaAsanas = null;
        let poop = null;

        Object.entries(entry).forEach(([key, val]) => {
          if (key === 'date' || key === 'supplements') return;
          if (key === 'yogaAsanas') yogaAsanas = val;
          else if (key === 'poop') poop = val;
          else if (typeof val === 'object') {
            extraDetails.push({ label: key, value: val });
          } else {
            routineData.push([capitalize(key), formatValue(val)]);
          }
        });

        // Daily Routine Table
        autoTable(doc, {
          startY: y,
          head: [['Daily Routine', 'Value']],
          body: routineData,
          styles: { fontSize: 8, cellPadding: 1.5 },
          headStyles: {
            fillColor: [255, 235, 205],
            textColor: 0,
            fontStyle: 'bold',
          },
          margin: { left: 10, right: 10 },
        });

        y = doc.lastAutoTable.finalY + 6;

        // Supplements Table
        const entrySupplements = Object.entries(entry.supplements || {}).map(([supp, taken]) => [
          supp,
          formatValue(taken),
        ]);

        if (entrySupplements.length > 0) {
          doc.setFontSize(10);
          doc.text('Supplements Taken:', 10, y);
          y += 4;

          autoTable(doc, {
            startY: y,
            head: [['Supplement', 'Taken']],
            body: entrySupplements,
            styles: { fontSize: 8, cellPadding: 1.5 },
            headStyles: { fillColor: [255, 235, 205], textColor: 0 },
            margin: { left: 10, right: 10 },
          });

          y = doc.lastAutoTable.finalY + 6;
        }

        // Yoga Asanas Table
        if (yogaAsanas && typeof yogaAsanas === 'object') {
          doc.setFontSize(10);
          doc.text('Yoga Asanas:', 10, y);
          y += 4;

          const yogaData = Object.entries(yogaAsanas).map(([session, info]) => {
            const doneVal = (info?.done || '').toLowerCase() === 'yes' ? 'Yes' : 'No';
            const notesVal = info?.notes?.trim() || '';
            return [capitalize(session), doneVal, notesVal];
          });

          autoTable(doc, {
            startY: y,
            head: [['Session', 'Done', 'Notes']],
            body: yogaData,
            styles: { fontSize: 8, cellPadding: 1.5 },
            headStyles: {
              fillColor: [255, 235, 205],
              textColor: 0,
              fontStyle: 'bold',
            },
            margin: { left: 10, right: 10 },
          });

          y = doc.lastAutoTable.finalY + 6;
        }

        // Poop Table
        if (poop && typeof poop === 'object') {
          doc.setFontSize(10);
          doc.text('Bowel Movement:', 10, y);
          y += 4;

          const poopData = [[
            poop.time || 'N/A',
            poop.consistency || 'N/A',
            poop.type || 'N/A',
            poop.notes || '',
          ]];

          autoTable(doc, {
            startY: y,
            head: [['Time', 'Consistency', 'Type', 'Notes']],
            body: poopData,
            styles: { fontSize: 8, cellPadding: 1.5 },
            headStyles: {
              fillColor: [255, 235, 205],
              textColor: 0,
              fontStyle: 'bold',
            },
            margin: { left: 10, right: 10 },
          });

          y = doc.lastAutoTable.finalY + 6;
        }

        // Extra Details
        if (extraDetails.length > 0) {
          doc.setFontSize(10);
          doc.text('Detailed Entries:', 10, y);
          y += 6;

          extraDetails.forEach(({ label, value }) => {
            doc.setFontSize(9);
            doc.text(`• ${capitalize(label)}:`, 12, y);
            y += 5;

            Object.entries(value).forEach(([k, v]) => {
              doc.text(`   - ${capitalize(k)}: ${formatValue(v)}`, 16, y);
              y += 4;
            });

            y += 4;
          });

          y += 6;
        }
      });
    });

    // Page numbers
    const totalPages = doc.internal.getNumberOfPages();
    for (let i = 1; i <= totalPages; i++) {
      doc.setPage(i);
      doc.setFontSize(9);
      doc.text(`Page ${i} of ${totalPages}`, 105, 290, null, null, 'center');
    }

    doc.save("gut-tracker-all-weeks.pdf");
  };

  return (
    <div className="mb-4 text-center">
      <button className="btn btn-primary" onClick={generatePDF}>
        Export All Weeks (PDF)
      </button>
    </div>
  );
}

export default WeeklyPdfExport;
